import React, {useState, useEffect} from 'react'
import axios from 'axios'

const Weather = (props) =>{
	const [completed,setCompleted] = useState(false)

	const getWeather = () =>{
		axios.get(`http://api.weatherstack.com/current?access_key=${"55dbce230d6ed0e4a89f35dc8fb8afbf"}&query=${props.country.capital}`)
		.then(response => {
			console.log(response.data)
			setCompleted(true)
			props.setWeather(response.data)
		})
		//.catch(error => console.log(error))
	}

	useEffect(getWeather,[])
	return completed ? (
		<>
			<h3>Weather in {props.country.capital}</h3>
			<p>
				<strong>temperature:</strong> {props.weather.current.temperature} celsius
			</p>
			<img src={props.weather.current.icon} alt={"Sky Representation"}/>
			<p>
				<strong>wind:</strong> {"X"} kph direction{" "}{"X"}
			</p>
		</>
	) : null 
}

const Content = (props) =>{
	const countriesToShow = () =>{
		if (props.filterPar==="") {
			return props.countries
		}
		else {
			let filterParUpper = props.filterPar.toUpperCase()
			let newlist = props.countries.filter(element => element.name.toUpperCase().search(filterParUpper)>-1)
			return newlist
		}
	}
	const showData = (country) =>{ 
		return(
			<>
			<h1>{country.name}</h1>
			<div>capital {country.capital}</div>
			<div>population {country.population}</div>
			<h2>languages</h2>
			<ul>
				{country.languages.map(element => <li key={element.name}>{element.name}</li>)}
			</ul>
			<img src={country.flag} alt="Country Flag" width="300" height="200"/>
			<Weather setWeather={props.setWeather} weather={props.weather} country={country}/>
			</>
		)
	}

	if (countriesToShow().length===1) {
		return (showData(countriesToShow()[0]))
	}
	else if (props.showButton!==""){
		return (showData(countriesToShow().filter(element => element.name===props.showButton )[0]))
	}
	else if (countriesToShow().length<=10){
		return(
			<div>{countriesToShow().map((element) => <li key={element.name}>{element.name} <button onClick={() => {
				props.setShowButton(element.name)
				props.setWeather(element.capital)
			}}>show</button></li>)} </div>
		)
	}
	else if (countriesToShow().length>10) {
		return(
			<div>Too many matches, specify another filter</div>
		)
	}
}

const App = () => {
	const [filterPar,setFilterPar] = useState("")
	const [countries,setCountries] = useState([])
	const [showButton, setShowButton] = useState("")
	const [weather, setWeather] = useState({})

	const getCountries = () => {
		axios.get("https://restcountries.eu/rest/v2/all").then(response => setCountries(response.data))
	}

	const changeFilterPar = (event) =>{
		setFilterPar(event.target.value)
		setShowButton("")
	}

	useEffect(getCountries, [])

	return(
		<div>
			<div>find countries <input value={filterPar} onChange={changeFilterPar}/></div>
			<Content countries={countries} filterPar={filterPar} setShowButton={setShowButton} showButton={showButton} weather={weather} setWeather={setWeather}/>
		</div>
	)
}

export default App;
