import React, { useState } from 'react'

const App = () => {
	const [persons, setPersons] = useState([
		{name: 'Arto Hellas'}
	])
	const [newName, setNewName] = useState("")

	const addPerson = (event) =>{
		event.preventDefault()
		const newPerson = {
			name: newName
		}
		for(let i=0;i<persons.length;i++){
			if (newPerson.name===persons[i].name){
				alert(newPerson.name + "is already added to the list")
				break
			}
			if (i===persons.length-1 && newPerson.name!=persons[i].name){
				setPersons(persons.concat(newPerson))
				setNewName("")
			}
		}
	}

	const changeInput = (event) =>{
		setNewName(event.target.value) 
	}

	return (
		<div>
			<h2>Phonebook</h2>
			<form onSubmit={addPerson}>
				<div>
					name: <input value={newName} onChange={changeInput}/>
				</div>
				<div>
					<button type="submit">add</button>
				</div>
			</form>
			<h2>Numbers</h2>
			{persons.map((element)=><li key={element.name}>{element.name}</li>)}
		</div>
	)
}

export default App
