import React, { useState } from 'react'

const App = () => {
	const [persons, setPersons] = useState([
		{name: "Arto Hellas", number: "040-123456"}
	])
	const [newName, setNewName] = useState("")
	const [newNumber, setNewNumber] = useState("")
	const [filter, setFilter] = useState("")

	const addPerson = (event) =>{
		event.preventDefault()
		const newPerson = {
			name: newName,
			number: newNumber
		}
		for(let i=0;i<persons.length;i++){
			if (newPerson.name===persons[i].name){
				alert(newPerson.name + "is already added to the list")
				break
			}
			if (i===persons.length-1 && newPerson.name!=persons[i].name){
				setPersons(persons.concat(newPerson))
				setNewName("")
				setNewNumber("")
			}
		}
	}

	const changeName = (event) =>{
		setNewName(event.target.value) 
	}

	const changeNumber = (event) =>{
		setNewNumber(event.target.value) 
	}

	const changeFilter = (event) =>{
		setFilter(event.target.value)
	}

	return (
		<div>
			<h2>Phonebook</h2>
			<div>filter shown with:<input value={filter} onChange={changeFilter}/></div>
			<h2>Add a new</h2>
			<form onSubmit={addPerson}>
				<div>
					name: <input value={newName} onChange={changeName}/>
				</div>
				<div>
					number: <input value={newNumber} onChange={changeNumber}/>
				</div>
				<div>
					<button type="submit">add</button>
				</div>
			</form>
			<h2>Numbers</h2>
			{persons.map((element)=><li key={element.name}>{element.name+"  "+element.number}</li>)}
		</div>
	)
}

export default App
