import React, { useState } from 'react'

const Filter = (props) =>{
	const changeFilterPar = (event) =>{
		props.setFilterPar(event.target.value)
	}

	return(
		<div>filter shown with:<input value={props.filterPar} onChange={changeFilterPar}/></div>
	)
}

const PersonForm = (props) =>{
	const addPerson = (event) =>{
		event.preventDefault()
		const newPerson = {
			name: newName,
			number: newNumber
		}
		for(let i=0;i<persons.length;i++){
			if (newPerson.name===persons[i].name){
				alert(newPerson.name + "is already added to the list")
				break
			}
			if (i===persons.length-1 && newPerson.name!==persons[i].name){
				setPersons(persons.concat(newPerson))
				setNewName("")
				setNewNumber("")
			}
		}
	}

	const changeName = (event) =>{
		props.setNewName(event.target.value) 
	}

	const changeNumber = (event) =>{
		props.setNewNumber(event.target.value) 
	}

	return(
		<form onSubmit={props.addPerson}>
			<div>
				name: <input value={props.newName} onChange={changeName}/>
			</div>
			<div>
				number: <input value={props.newNumber} onChange={changeNumber}/>
			</div>
			<div>
				<button type="submit">add</button>
			</div>
		</form>
	)
}

