import React, { useState } from 'react'

const Filter = ({filterPar , setFilterPar}) =>{
	const changeFilterPar = (event) =>{
		setFilterPar(event.target.value)
	}

	return(
		<div>filter shown with:<input value={filterPar} onChange={changeFilterPar}/></div>
	)
}

const PersonForm = (props) =>{
	const changeName = (event) =>{
		props.setNewName(event.target.value) 
	}

	const changeNumber = (event) =>{
		props.setNewNumber(event.target.value) 
	}

	return(
		<form onSubmit={props.addPerson}>
			<div>
				name: <input value={props.newName} onChange={changeName}/>
			</div>
			<div>
				number: <input value={props.newNumber} onChange={changeNumber}/>
			</div>
			<div>
				<button type="submit">add</button>
			</div>
		</form>
	)
}

const Persons = ({ persons, filterPar }) =>{
	const personsToShow = (persons) =>{
		if (filterPar==="") {
			return persons
		}
		else {
			let filterParUpper = filterPar.toUpperCase()
			let newlist = persons.filter((element) => element.name.toUpperCase().search(filterParUpper)>-1)
			return newlist
		}
	}

	return(
		<>
			{personsToShow(persons).map((element)=><li key={element.name}>{element.name+"  "+element.number}</li>)}
		</>
	)
}

const App = () => {
	const [persons, setPersons] = useState([
		{name: "Arto Hellas", number: "040-123456"}
	])
	const [newName, setNewName] = useState("")
	const [newNumber, setNewNumber] = useState("")
	const [filterPar, setFilterPar] = useState("")

	const addPerson = (event) =>{
		event.preventDefault()
		const newPerson = {
			name: newName,
			number: newNumber
		}
		for(let i=0;i<persons.length;i++){
			if (newPerson.name===persons[i].name){
				alert(newPerson.name + "is already added to the list")
				break
			}
			if (i===persons.length-1 && newPerson.name!==persons[i].name){
				setPersons(persons.concat(newPerson))
				setNewName("")
				setNewNumber("")
			}
		}
	}

	return (
		<div>
			<h2>Phonebook</h2>
			<Filter setFilterPar={setFilterPar} filterPar={filterPar}/>
			<h3>Add a new</h3>
			<PersonForm newName={newName} newNumber={newNumber} setNewName={setNewName} setNewNumber={setNewNumber} addPerson={addPerson}/>
			<h3>Numbers</h3>
			<Persons persons={persons} filterPar={filterPar}/>
		</div>
	)
}

export default App
