import React, {useState, useEffect} from 'react'
import Filter from './components/Filter'
import PersonForm from './components/PersonForm'
import Persons from './components/Persons'
import axios from 'axios'

const App = () => {
	const [persons, setPersons] = useState([
		{name: "Arto Hellas", number: "040-123456"}
	])
	const [newName, setNewName] = useState("")
	const [newNumber, setNewNumber] = useState("")
	const [filterPar, setFilterPar] = useState("")
	const [test, setTest] = useState({})

	const getPersons = () => {
		axios.get("http://localhost:3001/persons").then(response => setPersons(response.data))
	}
	const getTest = () => {
		axios.get("http://127.0.0.1:5000/").then(response => setTest(response.data))
	} 
	useEffect(getPersons,[])
	useEffect(getTest, [])

	return (
		<div>
			<h2>Phonebook</h2>
			<Filter setFilterPar={setFilterPar} filterPar={filterPar}/>
			<h3>Add a new</h3>
			<PersonForm newName={newName} newNumber={newNumber} persons={persons} setNewName={setNewName} setNewNumber={setNewNumber} setPersons={setPersons}/>
			<h3>Numbers</h3>
			<Persons persons={persons} filterPar={filterPar}/>
			<div>{test.rfid}</div>
		</div>
	)
}

export default App
